mapper.select:
SELECT OBJ_USR_CNT as obj_user_cnt, DAY_TOUCH_USR_CNT as day_touch_user_cnt, MONTH_TOUCH_USR_CNT as month_touch_user_cnt, TOTAL_TOUCH_USR_CNT as total_touch_user_cnt, DAY_SUCCESS_USR_CNT as day_success_user_cnt, MONTH_SUCCESS_USR_CNT as month_success_user_cnt, TOTAL_SUCCESS_USR_CNT as total_success_user_cnt, round(DAY_TOUCH_USR_CNT/OBJ_USR_CNT*100, 2) as day_cover_ratio, round(MONTH_TOUCH_USR_CNT/OBJ_USR_CNT*100, 2) as month_cover_ratio, round(TOTAL_TOUCH_USR_CNT/OBJ_USR_CNT*100, 2) as total_cover_ratio, ROUND(DAY_SUCCESS_USR_CNT/OBJ_USR_CNT*100, 2) as day_success_ratio, ROUND(MONTH_SUCCESS_USR_CNT/OBJ_USR_CNT*100, 2) as month_success_ratio, ROUND(TOTAL_SUCCESS_USR_CNT/OBJ_USR_CNT*100, 2) as total_success_ratio, DATA_TIME as data_time FROM( SELECT mkt.OBJ_USR_CNT, mkt.DAY_TOUCH_USR_CNT,mkt.MONTH_TOUCH_USR_CNT,mkt.TOTAL_TOUCH_USR_CNT, mkt.DAY_SUCCESS_USR_CNT,mkt.MONTH_SUCCESS_USR_CNT,mkt.TOTAL_SUCCESS_USR_CNT, mkt.DATA_TIME FROM rep_mkt_sum mkt join rep_chl_sum chl ON mkt.MKT_CAMPAIGN_NAME=chl.MKT_CAMPAIGN_NAME AND mkt.LAN_ID=chl.LAN_ID AND mkt.begin_time=chl.begin_time AND mkt.end_time=chl.end_time AND mkt.pre_lan_id=chl.pre_lan_id AND mkt.DATA_TIME=chl.DATA_TIME
mkt.pre_lan_id=-1 and mkt.lan_id=#{lan_id} and mkt.begin_time=#{begin_time} and mkt.end_time=#{end_time} and mkt.mkt_campaign_name=#{mkt_campaign_name} and chl.channel_id=#{channel_id}
)t;
